function replaceTitleContent(){document.querySelector('div.heading h1[role="heading"]').innerHTML="Log in"}function replaceButtonContent(){document.querySelector("button#next").innerHTML="Log in"}function fixUserNameLabel(){document.querySelector('label[for="email"]').innerHTML="Email"}function fixLabelPassword(){const e=document.querySelector('label[for="password"]');e.innerHTML="Password";const n=e.parentElement,t=n.parentElement;n.removeChild(e),t.insertBefore(e,n)}function fixForgotPasswordLink(){const e=document.querySelector("a#forgotPassword");e.parentElement.removeChild(e);document.querySelector("div.entry").appendChild(e),e.innerHTML="Forgot password?"}function styleInputs(){document.querySelector("input#email").placeholder="Email"}function changeErrorPlace(){[...document.querySelectorAll(".entry-item")].forEach((e=>{const n=e.querySelector(".error");e.removeChild(n),e.appendChild(n)}))}function fixCreateAccountSection(){const e=document.querySelector("div.create"),n=e.querySelector("a");document.querySelector("a#createNewAccount.link").href=n.href,e.style="display: none;"}function app(){replaceTitleContent(),replaceButtonContent(),changeErrorPlace(),fixUserNameLabel(),fixLabelPassword(),fixForgotPasswordLink(),fixCreateAccountSection(),styleInputs()}app();